### Advanced Type Guard Patterns

```typescript
// 1. Type guard builder pattern
function createTypeGuard<T>(
  check: (value: unknown) => boolean
): (value: unknown) => value is T {
  return (value: unknown): value is T => check(value);
}

const isPositiveNumber = createTypeGuard<number>(
  (value) => typeof value === "number" && value > 0
);

// 2. Composable type guards
function and<A, B>(
  guardA: (value: unknown) => value is A,
  guardB: (value: unknown) => value is B
): (value: unknown) => value is A & B {
  return (value: unknown): value is A & B => {
    return guardA(value) && guardB(value);
  };
}

interface HasId { id: number; }
interface HasName { name: string; }

const isHasId = (value: unknown): value is HasId =>
  typeof value === "object" && value !== null && "id" in value;

const isHasName = (value: unknown): value is HasName =>
  typeof value === "object" && value !== null && "name" in value;

const isEntity = and(isHasId, isHasName);

function processEntity(data: unknown) {
  if (isEntity(data)) {
    console.log(data.id, data.name); // data: HasId & HasName
  }
}

// 3. Schema-based validation
interface Schema<T> {
  validate(value: unknown): value is T;
}

class StringSchema implements Schema<string> {
  validate(value: unknown): value is string {
    return typeof value === "string";
  }
}

class NumberSchema implements Schema<number> {
  validate(value: unknown): value is number {
    return typeof value === "number";
  }
}

class ObjectSchema<T extends object> implements Schema<T> {
  constructor(private shape: { [K in keyof T]: Schema<T[K]> }) {}

  validate(value: unknown): value is T {
    if (typeof value !== "object" || value === null) {
      return false;
    }

    return Object.keys(this.shape).every(key => {
      const schema = this.shape[key as keyof T];
      return schema.validate((value as any)[key]);
    });
  }
}

