// 1. Recursive generic inference
type DeepReadonly<T> = {
  readonly [P in keyof T]: T[P] extends object ? DeepReadonly<T[P]> : T[P];
};

function deepFreeze<T extends object>(obj: T): DeepReadonly<T> {
  // implementation
  return obj as DeepReadonly<T>;
