// 2. Generic inference —Å builders
class QueryBuilder<T> {
  constructor(private table: string) {}

  where<K extends keyof T>(field: K, value: T[K]): this {
    // implementation
    return this;
  }

  select<K extends keyof T>(...fields: K[]): Pick<T, K>[] {
    // implementation
    return [] as Pick<T, K>[];
  }
}

