type Success<T> = {
  kind: "success"; // discriminant property
  value: T;
};

type Failure = {
  kind: "failure"; // discriminant property
  error: string;
};

type Result<T> = Success<T> | Failure;

// TypeScript –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ —Å—É–∂–∞–µ—Ç —Ç–∏–ø –Ω–∞ –æ—Å–Ω–æ–≤–µ discriminant
function handleResult<T>(result: Result<T>): T | null {
  if (result.kind === "success") {
    // TypeScript –∑–Ω–∞–µ—Ç, —á—Ç–æ —ç—Ç–æ Success<T>
    return result.value; // ‚úÖ
  } else {
    // TypeScript –∑–Ω–∞–µ—Ç, —á—Ç–æ —ç—Ç–æ Failure
    console.error(result.error); // ‚úÖ
    return null;
  }
}

// –ë–æ–ª–µ–µ —Å–ª–æ–∂–Ω—ã–π –ø—Ä–∏–º–µ—Ä - —Ç–∏–ø—ã —Å–æ–±—ã—Ç–∏–π
type ButtonClickEvent = {
  type: "button_click";
  buttonId: string;
  timestamp: number;
};

type InputChangeEvent = {
  type: "input_change";
  inputId: string;
