#### –ü—Ä–∏–º–µ—Ä 2: State Machines

```typescript
// –°–æ—Å—Ç–æ—è–Ω–∏—è –º–∞—à–∏–Ω—ã —Å–æ—Å—Ç–æ—è–Ω–∏–π –¥–ª—è –ø—Ä–æ—Ü–µ—Å—Å–∞ –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏—è –∑–∞–∫–∞–∑–∞
type IdleState = {
  status: "idle";
};

type LoadingState = {
  status: "loading";
  progress: number;
};

type ValidatingState = {
  status: "validating";
  fieldsToValidate: string[];
};

type SubmittingState = {
  status: "submitting";
  attemptNumber: number;
};

type SuccessState = {
  status: "success";
  orderId: string;
  confirmationCode: string;
};

type ErrorState = {
  status: "error";
  errorType: "validation" | "network" | "server";
  message: string;
  retryable: boolean;
};

type CheckoutState =
  | IdleState
  | LoadingState
  | ValidatingState
  | SubmittingState
  | SuccessState
  | ErrorState;

// State machine transitions
class CheckoutStateMachine {
  private state: CheckoutState = { status: "idle" };

  getState(): CheckoutState {
    return this.state;
  }

  startLoading() {
    if (this.state.status === "idle") {
      this.state = { status: "loading", progress: 0 };
    }
  }

  updateProgress(progress: number) {
    if (this.state.status === "loading") {
      this.state = { ...this.state, progress }; // ‚úÖ TypeScript –∑–Ω–∞–µ—Ç —Å—Ç—Ä—É–∫—Ç—É—Ä—É
    }
  }

  startValidation(fields: string[]) {
    if (this.state.status === "loading") {
      this.state = { status: "validating", fieldsToValidate: fields };
    }
  }

  submit() {
    if (this.state.status === "validating") {
      this.state = { status: "submitting", attemptNumber: 1 };
    }
  }

  retry() {
    if (this.state.status === "error" && this.state.retryable) {
      const attemptNumber =
        this.state.errorType === "network" ? 1 : 2; // –º–æ–∂–µ–º –ø–æ–ª—É—á–∏—Ç—å –¥–æ—Å—Ç—É–ø –∫ —Å–ø–µ—Ü–∏—Ñ–∏—á–Ω—ã–º —Å–≤–æ–π—Å—Ç–≤–∞–º
      this.state = { status: "submitting", attemptNumber };
    }
  }

  setSuccess(orderId: string, confirmationCode: string) {
    if (this.state.status === "submitting") {
      this.state = { status: "success", orderId, confirmationCode };
    }
  }

  setError(errorType: ErrorState["errorType"], message: string, retryable: boolean) {
    this.state = { status: "error", errorType, message, retryable };
  }

  // Render UI based on state
  renderUI(): string {
    const state = this.state;

    switch (state.status) {
      case "idle":
        return "Ready to checkout";
      case "loading":
        return `Loading... ${state.progress}%`;
      case "validating":
        return `Validating fields: ${state.fieldsToValidate.join(", ")}`;
      case "submitting":
        return `Submitting order (attempt ${state.attemptNumber})...`;
      case "success":
        return `Order successful! ID: ${state.orderId}, Code: ${state.confirmationCode}`;
      case "error":
        return `Error (${state.errorType}): ${state.message}${
          state.retryable ? " - You can retry" : ""
        }`;
      default:
        // Exhaustiveness check
        const _exhaustive: never = state;
        return _exhaustive;
    }
  }
}
```

