const HttpHeader = {
  ContentType: 'Content-Type',
  Authorization: 'Authorization',
  Accept: 'Accept',
  CacheControl: 'Cache-Control',
  UserAgent: 'User-Agent'
} as const;

type HttpHeaderKey = keyof typeof HttpHeader;
type HttpHeaderValue = typeof HttpHeader[HttpHeaderKey];

// –£—Ç–∏–ª–∏—Ç—ã –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å headers
const HttpHeaderUtils = {
  keys: Object.keys(HttpHeader) as HttpHeaderKey[],
  values: Object.values(HttpHeader),

  has(key: string): key is HttpHeaderKey {
    return key in HttpHeader;
  },

  isValid(value: string): value is HttpHeaderValue {
    return Object.values(HttpHeader).includes(value as HttpHeaderValue);
  }
};

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
