// Discriminated union –≤–º–µ—Å—Ç–æ enum
type RequestState =
  | { status: 'idle' }
  | { status: 'loading'; progress: number }
  | { status: 'success'; data: unknown }
  | { status: 'error'; error: Error };

// –í—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏
const RequestState = {
  idle: (): RequestState => ({ status: 'idle' }),
  loading: (progress: number): RequestState => ({ status: 'loading', progress }),
  success: (data: unknown): RequestState => ({ status: 'success', data }),
  error: (error: Error): RequestState => ({ status: 'error', error })
};

class DataFetcher {
  private state: RequestState = RequestState.idle();

  async fetch(url: string): Promise<void> {
    this.state = RequestState.loading(0);

    try {
      const response = await fetch(url);
      const data = await response.json();
      this.state = RequestState.success(data);
    } catch (error) {
      this.state = RequestState.error(error as Error);
    }
  }

  getState(): RequestState {
    return this.state;
  }

  // Type-safe –æ–±—Ä–∞–±–æ—Ç–∫–∞ —Å–æ—Å—Ç–æ—è–Ω–∏–π
  render(): string {
    switch (this.state.status) {
      case 'idle':
        return 'Ready to fetch';
      case 'loading':
        return `Loading... ${this.state.progress}%`;
      case 'success':
        return `Data: ${JSON.stringify(this.state.data)}`;
      case 'error':
        return `Error: ${this.state.error.message}`;
    }
  }
}
```

#### –ü—Ä–∏–º–µ—Ä 6: –ö–æ–º–±–∏–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ –ø–æ–¥—Ö–æ–¥–æ–≤

