// Overloading –¥–ª—è —Ä–∞–∑–Ω—ã—Ö —Å—Ç—Ä—É–∫—Ç—É—Ä –¥–∞–Ω–Ω—ã—Ö
interface SearchByIdQuery {
  type: 'id';
  id: number;
}

interface SearchByNameQuery {
  type: 'name';
  name: string;
}

interface SearchByTagsQuery {
  type: 'tags';
  tags: string[];
}

type SearchQuery = SearchByIdQuery | SearchByNameQuery | SearchByTagsQuery;

interface Item {
  id: number;
  name: string;
  tags: string[];
