// Overload signatures - —Ç–æ—á–Ω—ã–µ —Ç–∏–ø—ã –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —Å–ª—É—á–∞—è
function search(query: SearchByIdQuery): Item | undefined;
function search(query: SearchByNameQuery): Item[];
function search(query: SearchByTagsQuery): Item[];

// Implementation signature
function search(query: SearchQuery): Item | Item[] | undefined {
  const items: Item[] = [
    { id: 1, name: 'TypeScript Book', tags: ['programming', 'typescript'] },
    { id: 2, name: 'JavaScript Guide', tags: ['programming', 'javascript'] }
  ];

  switch (query.type) {
    case 'id':
      return items.find(item => item.id === query.id);
    case 'name':
      return items.filter(item => item.name.includes(query.name));
    case 'tags':
      return items.filter(item =>
        query.tags.some(tag => item.tags.includes(tag))
      );
  }
}

