```typescript
// Merge —Å —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ–º —Ç–∏–ø–æ–≤
function merge<T extends object, U extends object>(
  obj1: T,
  obj2: U
): T & U {
