// –ì–ª—É–±–æ–∫–æ–µ –∫–ª–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ —Å generic constraints
type Primitive = string | number | boolean | null | undefined;

type DeepCloneable =
  | Primitive
  | Date
  | RegExp
  | Array<DeepCloneable>
  | { [key: string]: DeepCloneable };

function deepClone<T extends DeepCloneable>(value: T): T {
  if (value === null || typeof value !== 'object') {
    return value;
  }

  if (value instanceof Date) {
    return new Date(value.getTime()) as T;
  }

  if (value instanceof RegExp) {
    return new RegExp(value.source, value.flags) as T;
  }

  if (Array.isArray(value)) {
    return value.map(item => deepClone(item)) as T;
  }

  const cloned = {} as T;
  for (const key in value) {
    if (value.hasOwnProperty(key)) {
      cloned[key] = deepClone(value[key]);
    }
  }
  return cloned;
}

const original = {
  name: 'John',
  date: new Date(),
  tags: ['typescript', 'programming'],
  nested: { level: 1 }
};

const copy = deepClone(original);
copy.tags.push('advanced'); // –ù–µ –≤–ª–∏—è–µ—Ç –Ω–∞ original

// Generic function —Å mapped types
function pick<T, K extends keyof T>(obj: T, ...keys: K[]): Pick<T, K> {
  const result = {} as Pick<T, K>;
  for (const key of keys) {
    result[key] = obj[key];
  }
  return result;
}

