#### –ü—Ä–∏–º–µ—Ä 1: –ö–æ–º–±–∏–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ typeof –∏ instanceof

```typescript
type JSONValue =
  | string
  | number
  | boolean
  | null
  | JSONValue[]
  | { [key: string]: JSONValue };

function stringifyJSON(value: JSONValue): string {
  // typeof –¥–ª—è –ø—Ä–∏–º–∏—Ç–∏–≤–æ–≤
  if (typeof value === 'string') {
    return `"${value}"`;
  }

  if (typeof value === 'number' || typeof value === 'boolean') {
    return String(value);
  }

  if (value === null) {
    return 'null';
  }

  // instanceof –¥–ª—è Array
  if (value instanceof Array) {
    const items = value.map(v => stringifyJSON(v));
    return `[${items.join(',')}]`;
  }

  // –û–±—ä–µ–∫—Ç
  const entries = Object.entries(value).map(
    ([k, v]) => `"${k}":${stringifyJSON(v)}`
  );
  return `{${entries.join(',')}}`;
}

