#### –ü—Ä–∏–º–µ—Ä 2: –°–ª–æ–∂–Ω—ã–µ type guards –¥–ª—è –≤–∞–ª–∏–¥–∞—Ü–∏–∏

```typescript
// Composable type guards
interface Point {
  x: number;
  y: number;
}

interface Circle extends Point {
  radius: number;
}

interface Rectangle {
  topLeft: Point;
  bottomRight: Point;
}

// –ë–∞–∑–æ–≤—ã–µ type guards
function isPoint(obj: unknown): obj is Point {
  return (
    typeof obj === 'object' &&
    obj !== null &&
    'x' in obj &&
    'y' in obj &&
    typeof (obj as Point).x === 'number' &&
    typeof (obj as Point).y === 'number'
  );
}

function isCircle(obj: unknown): obj is Circle {
  return (
    isPoint(obj) &&
    'radius' in obj &&
    typeof (obj as Circle).radius === 'number'
  );
}

function isRectangle(obj: unknown): obj is Rectangle {
  return (
    typeof obj === 'object' &&
    obj !== null &&
    'topLeft' in obj &&
    'bottomRight' in obj &&
    isPoint((obj as Rectangle).topLeft) &&
    isPoint((obj as Rectangle).bottomRight)
  );
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
function getArea(shape: unknown): number {
  if (isCircle(shape)) {
    return Math.PI * shape.radius ** 2;
  }

  if (isRectangle(shape)) {
    const width = shape.bottomRight.x - shape.topLeft.x;
    const height = shape.bottomRight.y - shape.topLeft.y;
    return width * height;
  }

  throw new Error('Unknown shape');
}

// Generic validator
function createValidator<T>(
  schema: {
    [K in keyof T]: (value: unknown) => value is T[K];
  }
): (obj: unknown) => obj is T {
  return (obj: unknown): obj is T => {
    if (typeof obj !== 'object' || obj === null) {
      return false;
    }

    for (const key in schema) {
      if (!(key in obj)) return false;
      if (!schema[key]((obj as any)[key])) return false;
    }

    return true;
  };
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
interface User {
  name: string;
  age: number;
  active: boolean;
