#### –ü—Ä–∏–º–µ—Ä 3: Discriminated Unions –¥–ª—è State Management

```typescript
// State machine —Å discriminated unions
interface IdleState {
  status: 'idle';
}

interface LoadingState {
  status: 'loading';
  startedAt: Date;
  progress: number;
}

interface SuccessState<T> {
  status: 'success';
  data: T;
  loadedAt: Date;
}

interface ErrorState {
  status: 'error';
  error: Error;
  retryCount: number;
}

type AsyncState<T> = IdleState | LoadingState | SuccessState<T> | ErrorState;

class DataFetcher<T> {
  private state: AsyncState<T> = { status: 'idle' };

  async fetch(url: string): Promise<void> {
    this.state = {
      status: 'loading',
      startedAt: new Date(),
      progress: 0
    };

    try {
      const response = await fetch(url);
      const data = await response.json();

      this.state = {
        status: 'success',
        data,
        loadedAt: new Date()
      };
    } catch (error) {
      this.state = {
        status: 'error',
        error: error as Error,
        retryCount: 0
      };
    }
  }

  getStatus(): string {
    switch (this.state.status) {
      case 'idle':
        return 'Ready to fetch';

      case 'loading':
        const elapsed = Date.now() - this.state.startedAt.getTime();
        return `Loading... ${this.state.progress}% (${elapsed}ms)`;

      case 'success':
        return `Loaded at ${this.state.loadedAt.toISOString()}`;

      case 'error':
        return `Error: ${this.state.error.message} (retries: ${this.state.retryCount})`;

      default:
        // Exhaustiveness check
        const _: never = this.state;
        return _;
    }
  }

  getData(): T | undefined {
    // Type narrowing —Å return
    if (this.state.status === 'success') {
      return this.state.data; // this.state: SuccessState<T>
    }
    return undefined;
  }
}

