function assertArrayLength<T>(
  arr: T[],
  min: number,
  max?: number
): asserts arr is T[] {
  if (arr.length < min) {
    throw new Error(`Array must have at least ${min} elements`);
  }
  if (max !== undefined && arr.length > max) {
    throw new Error(`Array must have at most ${max} elements`);
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –≤ —Ñ—É–Ω–∫—Ü–∏–∏
interface CreateUserParams {
