  assert(Array.isArray(input), 'Input must be an array');
  assertArrayLength(input, 1, 10);

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∫–∞–∂–¥–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞
  input.forEach((item, index) => {
