#### –ü—Ä–∏–º–µ—Ä 6: Tagged Template —Å Type Narrowing

```typescript
// Discriminated union –¥–ª—è —Ä–∞–∑–Ω—ã—Ö —Ç–∏–ø–æ–≤ —Ñ–æ—Ä–º
interface TextInput {
  type: 'text';
  value: string;
  placeholder?: string;
}

interface NumberInput {
  type: 'number';
  value: number;
  min?: number;
  max?: number;
}

interface CheckboxInput {
  type: 'checkbox';
  checked: boolean;
}

interface SelectInput {
  type: 'select';
  value: string;
  options: string[];
}

type FormInput = TextInput | NumberInput | CheckboxInput | SelectInput;

class FormValidator {
  validate(input: FormInput): boolean {
    switch (input.type) {
      case 'text':
        return input.value.length > 0;

      case 'number':
        const value = input.value;
        if (input.min !== undefined && value < input.min) return false;
        if (input.max !== undefined && value > input.max) return false;
        return true;

      case 'checkbox':
        return input.checked;

      case 'select':
        return input.options.includes(input.value);

      default:
        const _exhaustive: never = input;
        return false;
    }
  }

  getErrorMessage(input: FormInput): string | null {
    if (this.validate(input)) {
      return null;
    }

    switch (input.type) {
      case 'text':
        return 'Text field cannot be empty';

      case 'number':
        if (input.min !== undefined && input.value < input.min) {
          return `Value must be at least ${input.min}`;
        }
        if (input.max !== undefined && input.value > input.max) {
          return `Value must be at most ${input.max}`;
        }
        return 'Invalid number';

      case 'checkbox':
        return 'Checkbox must be checked';

      case 'select':
        return `Value must be one of: ${input.options.join(', ')}`;

      default:
        const _exhaustive: never = input;
        return null;
    }
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const validator = new FormValidator();

const textInput: TextInput = {
  type: 'text',
  value: '',
  placeholder: 'Enter name'
};

const numberInput: NumberInput = {
  type: 'number',
  value: 5,
  min: 0,
  max: 10
};

console.log(validator.validate(textInput)); // false
console.log(validator.getErrorMessage(textInput)); // "Text field cannot be empty"

console.log(validator.validate(numberInput)); // true
```

