// Generic type guard –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏ —Å–≤–æ–π—Å—Ç–≤
function hasProperty<T, K extends PropertyKey>(
  obj: T,
  key: K
): obj is T & Record<K, unknown> {
  return typeof obj === 'object' && obj !== null && key in obj;
}

function getProperty<T, K extends PropertyKey>(
  obj: T,
  key: K
): K extends keyof T ? T[K] : unknown {
  if (hasProperty(obj, key)) {
    return obj[key] as any;
  }
  return undefined as any;
}

const user = { name: 'John', age: 30 };
const name = getProperty(user, 'name'); // string
const age = getProperty(user, 'age'); // number
const unknown = getProperty(user, 'unknown'); // unknown

// isInstanceOf helper
function isInstanceOf<T>(
  value: unknown,
  constructor: new (...args: any[]) => T
): value is T {
  return value instanceof constructor;
}

const date: unknown = new Date();
if (isInstanceOf(date, Date)) {
  console.log(date.toISOString()); // date: Date
}
```

