#### 3. Type-Safe Event Emitter

```typescript
// –ò–∑–≤–ª–µ—á–µ–Ω–∏–µ —Ç–∏–ø–æ–≤ —Å–æ–±—ã—Ç–∏–π –∏ –∏—Ö payload
type EventMap = {
  'user:login': { userId: string; timestamp: number };
  'user:logout': { userId: string };
  'data:update': { data: any[] };
};

type EventNames = keyof EventMap;

type EventPayload<E extends EventNames> = EventMap[E];

class TypedEventEmitter<Events extends Record<string, any>> {
  private listeners: {
    [E in keyof Events]?: Array<(payload: Events[E]) => void>;
  } = {};

  on<E extends keyof Events>(
    event: E,
    callback: (payload: Events[E]) => void
  ): void {
    if (!this.listeners[event]) {
      this.listeners[event] = [];
    }
    this.listeners[event]!.push(callback);
  }

  emit<E extends keyof Events>(event: E, payload: Events[E]): void {
    this.listeners[event]?.forEach(callback => callback(payload));
  }
}

const emitter = new TypedEventEmitter<EventMap>();

// ‚úÖ Type-safe
emitter.on('user:login', (payload) => {
  console.log(payload.userId, payload.timestamp);
});

// ‚ùå Error: –Ω–µ–≤–µ—Ä–Ω—ã–π —Ç–∏–ø payload
// emitter.emit('user:login', { userId: 123 });

// ‚úÖ Correct
emitter.emit('user:login', { userId: '123', timestamp: Date.now() });
```

