#### 4. Proxy –¥–ª—è –æ–±—ä–µ–∫—Ç–∞ —Å —Å–æ–±—ã—Ç–∏—è–º–∏

```typescript
type EventMap<T> = {
  [K in keyof T as `on${Capitalize<K & string>}Change`]: (
    oldValue: T[K],
    newValue: T[K]
  ) => void;
};

type ReactiveObject<T> = T & EventMap<T>;

function createReactive<T extends object>(obj: T): ReactiveObject<T> {
  const handlers: any = {};

  return new Proxy(obj as ReactiveObject<T>, {
    set(target: any, prop: string, value: any) {
      const oldValue = target[prop];
      target[prop] = value;

      const eventName = `on${prop.charAt(0).toUpperCase() + prop.slice(1)}Change`;
      if (handlers[eventName]) {
        handlers[eventName](oldValue, value);
      }

      return true;
    },
    get(target: any, prop: string) {
      if (prop.startsWith('on') && prop.endsWith('Change')) {
        return (callback: Function) => {
          handlers[prop] = callback;
        };
      }
      return target[prop];
    }
  });
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const state = createReactive({ count: 0, name: 'Test' });

state.onCountChange((old, new_) => {
  console.log(`Count changed from ${old} to ${new_}`);
});

state.count = 5;  // –¢—Ä–∏–≥–≥–µ—Ä–∏—Ç —Å–æ–±—ã—Ç–∏–µ
```

