// { userId: string; postId: string }

// –ü—Ä–æ–≤–µ—Ä–∫–∞ —Ñ–æ—Ä–º–∞—Ç–∞ email
type IsEmail<T extends string> =
  T extends `${infer Local}@${infer Domain}.${infer TLD}`
    ? TLD extends '' ? false : true
    : false;

type Valid = IsEmail<"user@example.com">;  // true
