#### 2. Type-Safe Route Paths

```typescript
// –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –º–∞—Ä—à—Ä—É—Ç–æ–≤
type Routes = {
  '/': {};
  '/users': {};
  '/users/:userId': { userId: string };
  '/users/:userId/posts': { userId: string };
  '/users/:userId/posts/:postId': { userId: string; postId: string };
  '/posts/:postId/comments/:commentId': { postId: string; commentId: string };
};

// –ò–∑–≤–ª–µ—á–µ–Ω–∏–µ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤ –∏–∑ –ø—É—Ç–∏
type ExtractParams<T extends string> =
  T extends `${infer Start}/:${infer Param}/${infer Rest}`
    ? { [K in Param]: string } & ExtractParams<`/${Rest}`>
    : T extends `${infer Start}/:${infer Param}`
    ? { [K in Param]: string }
    : {};

// Type-safe –Ω–∞–≤–∏–≥–∞—Ü–∏—è
type Navigate = <Path extends keyof Routes>(
  path: Path,
  ...args: {} extends Routes[Path] ? [] : [params: Routes[Path]]
) => void;

declare const navigate: Navigate;

// ‚úÖ –†–∞–±–æ—Ç–∞–µ—Ç
navigate('/');
navigate('/users');
navigate('/users/:userId', { userId: '123' });
navigate('/users/:userId/posts/:postId', { userId: '123', postId: '456' });

// ‚ùå –û—à–∏–±–∫–∏
// navigate('/users/:userId');  // Error: –ø–∞—Ä–∞–º–µ—Ç—Ä—ã –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã
// navigate('/users/:userId', { userId: 123 });  // Error: –Ω–µ–≤–µ—Ä–Ω—ã–π —Ç–∏–ø
// navigate('/invalid/path');  // Error: –Ω–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π –ø—É—Ç—å

// –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –ø—É—Ç–µ–π –∏–∑ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
type BuildPath<
  Path extends string,
  Params extends Record<string, string>
> = Path extends `${infer Start}/:${infer Param}/${infer Rest}`
  ? `${Start}/${Params[Param]}/${BuildPath<Rest, Params>}`
  : Path extends `${infer Start}/:${infer Param}`
  ? `${Start}/${Params[Param]}`
  : Path;

function buildPath<Path extends string>(
  template: Path,
  params: ExtractParams<Path>
): BuildPath<Path, ExtractParams<Path>> {
  let result: string = template;
  for (const [key, value] of Object.entries(params)) {
    result = result.replace(`:${key}`, value as string);
  }
  return result as any;
}

const userPostPath = buildPath('/users/:userId/posts/:postId', {
  userId: '123',
  postId: '456'
});
// "/users/123/posts/456"
```

#### 3. Type-Safe Event System

```typescript
// –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Å–æ–±—ã—Ç–∏–π
interface EventPayloads {
  'user:login': { userId: string; timestamp: number };
  'user:logout': { userId: string };
  'post:create': { postId: string; authorId: string; title: string };
  'post:update': { postId: string; fields: string[] };
  'post:delete': { postId: string };
}

// –ì–µ–Ω–µ—Ä–∞—Ü–∏—è —Ç–∏–ø–æ–≤ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–æ–≤
type EventHandler<T> = (payload: T) => void | Promise<void>;

type EventHandlers = {
  [K in keyof EventPayloads as `on${Capitalize<K>}`]: EventHandler<EventPayloads[K]>;
};

// {
//   onUserLogin: EventHandler<{ userId: string; timestamp: number }>;
//   onUserLogout: EventHandler<{ userId: string }>;
//   onPostCreate: EventHandler<{ postId: string; authorId: string; title: string }>;
//   ...
// }

// Wildcard —Å–æ–±—ã—Ç–∏—è
type WildcardPattern<T extends string> =
  T extends `${infer Prefix}:${infer Suffix}`
    ? `${Prefix}:*` | T
    : T;

type EventPattern = WildcardPattern<keyof EventPayloads>;
// "user:login" | "user:logout" | "user:*" | "post:create" | "post:*" | ...

// Event emitter —Å –ø–æ–¥–¥–µ—Ä–∂–∫–æ–π wildcards
class TypedEventEmitter {
  private listeners: Map<string, Set<Function>> = new Map();

  on<E extends keyof EventPayloads>(
    event: E | WildcardPattern<E>,
    handler: EventHandler<EventPayloads[E]>
  ): void {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, new Set());
    }
    this.listeners.get(event)!.add(handler);
  }

  emit<E extends keyof EventPayloads>(
    event: E,
    payload: EventPayloads[E]
  ): void {
    // –¢–æ—á–Ω–æ–µ —Å–æ–≤–ø–∞–¥–µ–Ω–∏–µ
    this.listeners.get(event)?.forEach(handler => handler(payload));

    // Wildcard —Å–æ–≤–ø–∞–¥–µ–Ω–∏–µ
    const [prefix] = (event as string).split(':');
    const wildcardEvent = `${prefix}:*`;
    this.listeners.get(wildcardEvent)?.forEach(handler => handler(payload));
  }
}

const emitter = new TypedEventEmitter();

// ‚úÖ Type-safe
emitter.on('user:login', ({ userId, timestamp }) => {
  console.log(`User ${userId} logged in at ${timestamp}`);
});

emitter.on('user:*', (payload) => {
  console.log('Any user event:', payload);
});

emitter.emit('user:login', { userId: '123', timestamp: Date.now() });
```

#### 4. Database Query Builder

```typescript
// –°—Ö–µ–º–∞ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö
interface DatabaseSchema {
