// –û–ø–µ—Ä–∞—Ç–æ—Ä—ã —Å—Ä–∞–≤–Ω–µ–Ω–∏—è
type Operator = 'eq' | 'neq' | 'gt' | 'gte' | 'lt' | 'lte' | 'like';
type WhereClause<T extends string, O extends Operator> = `${T}_${O}`;

// –ì–µ–Ω–µ—Ä–∞—Ü–∏—è —É—Å–ª–æ–≤–∏–π –¥–ª—è –≤—Å–µ—Ö –ø–æ–ª–µ–π
type TableWhereFields<T> = {
