// Query builder
class QueryBuilder<Table extends keyof DatabaseSchema> {
  constructor(private table: Table) {}

  where(conditions: Partial<TableWhereFields<DatabaseSchema[Table]>>) {
    // –†–µ–∞–ª–∏–∑–∞—Ü–∏—è
    return this;
  }

  select<K extends keyof DatabaseSchema[Table]>(...fields: K[]) {
    // –†–µ–∞–ª–∏–∑–∞—Ü–∏—è
