type EnvVarName =
  | 'API_URL'
  | 'API_KEY'
  | 'FEATURE_FLAG_NEW_UI'
  | 'DEBUG_MODE';

// –ü–æ–ª–Ω—ã–µ –∏–º–µ–Ω–∞ —Å –ø—Ä–µ—Ñ–∏–∫—Å–∞–º–∏
type EnvVar = `${EnvPrefix}_${EnvVarName}`;

// –¢–∏–ø—ã –∑–Ω–∞—á–µ–Ω–∏–π
type EnvVarValue<T extends EnvVar> =
  T extends `${string}_API_URL` | `${string}_API_KEY` ? string :
  T extends `${string}_FEATURE_FLAG_${string}` ? 'true' | 'false' :
  T extends `${string}_DEBUG_MODE` ? 'true' | 'false' :
  string;

// Type-safe –ø—Ä–æ—Ü–µ—Å—Å.env
type ProcessEnv = {
  [K in EnvVar]?: EnvVarValue<K>;
};

