// keyof —Å generic constraints
function updateProperty<T, K extends keyof T>(
  obj: T,
  key: K,
  value: T[K]
): T {
  return { ...obj, [key]: value };
}

const updatedUser = updateProperty(user, "age", 31);
// const invalid = updateProperty(user, "age", "31"); // ‚ùå Error: string != number
