### Real-world Use Cases

#### Use Case 1: Type-safe Redux actions

```typescript
const ActionType = {
  ADD_TODO: "ADD_TODO",
  REMOVE_TODO: "REMOVE_TODO",
  TOGGLE_TODO: "TOGGLE_TODO",
  SET_FILTER: "SET_FILTER"
} as const;

type ActionType = typeof ActionType[keyof typeof ActionType];

// Action creators
interface AddTodoAction {
  type: typeof ActionType.ADD_TODO;
  payload: { id: string; text: string };
}

interface RemoveTodoAction {
  type: typeof ActionType.REMOVE_TODO;
  payload: { id: string };
}

interface ToggleTodoAction {
  type: typeof ActionType.TOGGLE_TODO;
  payload: { id: string };
}

interface SetFilterAction {
  type: typeof ActionType.SET_FILTER;
  payload: { filter: "all" | "active" | "completed" };
}

type Action =
  | AddTodoAction
  | RemoveTodoAction
  | ToggleTodoAction
  | SetFilterAction;

// Reducer —Å –∞–≤—Ç–æ–¥–æ–ø–æ–ª–Ω–µ–Ω–∏–µ–º
function reducer(state: State, action: Action): State {
  switch (action.type) {
    case ActionType.ADD_TODO:
      // action.payload –∏–º–µ–µ—Ç —Ç–∏–ø { id: string; text: string }
      return { ...state, todos: [...state.todos, action.payload] };

    case ActionType.REMOVE_TODO:
      // action.payload –∏–º–µ–µ—Ç —Ç–∏–ø { id: string }
      return {
        ...state,
        todos: state.todos.filter(t => t.id !== action.payload.id)
      };

    case ActionType.TOGGLE_TODO:
      return {
        ...state,
        todos: state.todos.map(t =>
          t.id === action.payload.id ? { ...t, completed: !t.completed } : t
        )
      };

    case ActionType.SET_FILTER:
      return { ...state, filter: action.payload.filter };

    default:
      return state;
  }
}
```

