#### Use Case 3: Form field validation

```typescript
const VALIDATORS = {
  required: (value: unknown) => value !== null && value !== undefined && value !== "",
  email: (value: unknown) =>
    typeof value === "string" && /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value),
  minLength: (min: number) => (value: unknown) =>
    typeof value === "string" && value.length >= min,
  maxLength: (max: number) => (value: unknown) =>
    typeof value === "string" && value.length <= max,
  pattern: (regex: RegExp) => (value: unknown) =>
    typeof value === "string" && regex.test(value)
} as const;

type ValidatorName = keyof typeof VALIDATORS;
type Validator = typeof VALIDATORS[ValidatorName];

// Form field configuration
interface FieldConfig {
  name: string;
  label: string;
  validators: ValidatorName[];
}

const formConfig: FieldConfig[] = [
  {
    name: "email",
    label: "Email",
    validators: ["required", "email"]
  },
  {
    name: "password",
    label: "Password",
    validators: ["required", "minLength"]
  }
];

// Type-safe validation
function validateField(
  value: unknown,
  validatorNames: ValidatorName[]
): boolean {
  return validatorNames.every(name => {
    const validator = VALIDATORS[name];
    if (typeof validator === "function") {
      return validator(value);
    }
    return true;
  });
}
```

