type AsyncGetter<out T> = () => Promise<T>; // Covariant
type AsyncSetter<in T> = (value: T) => Promise<void>; // Contravariant
type AsyncStorage<T> = {
  get: AsyncGetter<T>;
  set: AsyncSetter<T>;
}; // Invariant

const dogStorage: AsyncStorage<Dog> = {
