interface Reader<out T> {
  read(): T;
}

interface Writer<in T> {
  write(value: T): void;
}

interface ReadWriter<T> extends Reader<T>, Writer<T> {}

// ‚úÖ –•–û–†–û–®–û: –Ø–≤–Ω–æ —É–∫–∞–∑—ã–≤–∞–π—Ç–µ variance (TS 4.7+)
interface EventEmitter<in out T> {
  emit(event: T): void;  // in position
