}

// ‚úÖ –•–û–†–û–®–û: –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ strictFunctionTypes
// tsconfig.json
{
  "compilerOptions": {
    "strictFunctionTypes": true,
    "strict": true
  }
}

// ‚úÖ –•–û–†–û–®–û: –ü–æ–Ω–∏–º–∞–π—Ç–µ variance –ø—Ä–∏ –ø—Ä–æ–µ–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ API
class EventBus<T> {
  private handlers: Array<(event: T) => void> = [];

  // Contravariant: –ø—Ä–∏–Ω–∏–º–∞–µ–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –±–æ–ª–µ–µ –æ–±—â–∏—Ö —Ç–∏–ø–æ–≤
  subscribe(handler: (event: T) => void): void {
    this.handlers.push(handler);
  }

  // Invariant: —Ç–æ—á–Ω–æ–µ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏–µ —Ç–∏–ø–∞
  publish(event: T): void {
    this.handlers.forEach(h => h(event));
  }
}

// ‚úÖ –•–û–†–û–®–û: –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ utility types —Å –ø—Ä–∞–≤–∏–ª—å–Ω–æ–π variance
type Getter<T> = () => T;                    // Covariant
type Setter<T> = (value: T) => void;         // Contravariant
type Property<T> = { get: Getter<T>; set: Setter<T> }; // Invariant

// ‚úÖ –•–û–†–û–®–û: –î–æ–∫—É–º–µ–Ω—Ç–∏—Ä—É–π—Ç–µ –æ–∂–∏–¥–∞–µ–º—É—é variance
/**
 * Producer interface - covariant in T
 * Safe to assign Producer<Dog> to Producer<Animal>
 */
interface IProducer<out T> {
  produce(): T;
}

/**
 * Consumer interface - contravariant in T
 * Safe to assign Consumer<Animal> to Consumer<Dog>
 */
interface IConsumer<in T> {
  consume(item: T): void;
}
```

### –ü—Ä–∞–∫—Ç–∏—á–µ—Å–∫–∏–µ –ø—Ä–∏–º–µ—Ä—ã

```typescript
// üéØ –°–∏—Å—Ç–µ–º–∞ —Å–æ–±—ã—Ç–∏–π —Å –ø—Ä–∞–≤–∏–ª—å–Ω–æ–π variance
type EventMap = {
  click: { x: number; y: number };
  keypress: { key: string };
  custom: { data: any };
};

type EventListener<T> = (event: T) => void;

class TypedEventEmitter<Events extends EventMap> {
  private listeners: {
    [K in keyof Events]?: Array<EventListener<Events[K]>>;
  } = {};

  // Contravariant –≤ —Ç–∏–ø–µ —Å–æ–±—ã—Ç–∏—è
  on<K extends keyof Events>(
    event: K,
    listener: EventListener<Events[K]>
  ): void {
    if (!this.listeners[event]) {
      this.listeners[event] = [];
    }
    this.listeners[event]!.push(listener);
  }

  // Invariant - —Ç–æ—á–Ω–æ–µ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏–µ
  emit<K extends keyof Events>(event: K, data: Events[K]): void {
    const eventListeners = this.listeners[event];
    if (eventListeners) {
      eventListeners.forEach(listener => listener(data));
    }
  }
}

const emitter = new TypedEventEmitter<EventMap>();

// Contravariance –ø–æ–∑–≤–æ–ª—è–µ—Ç –æ–±—â–∏–µ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏
const generalHandler: EventListener<{ x: number; y: number }> = (e) => {
  console.log(`Position: ${e.x}, ${e.y}`);
};

emitter.on('click', generalHandler); // ‚úÖ OK

