}

// Covariant –≤ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º–æ–º —Ç–∏–ø–µ
interface IRepository<out T extends Entity> {
  findById(id: number): Promise<T | null>;
  findAll(): Promise<T[]>;
}

// Contravariant –≤ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞—Ö
interface IWriter<in T extends Entity> {
  save(entity: T): Promise<void>;
  delete(entity: T): Promise<void>;
}

// Invariant –∫–æ–º–±–∏–Ω–∞—Ü–∏—è
interface IFullRepository<T extends Entity>
  extends IRepository<T>, IWriter<T> {}

class UserRepository implements IFullRepository<User> {
  async findById(id: number): Promise<User | null> {
    // implementation
    return null;
  }

  async findAll(): Promise<User[]> {
    return [];
  }

  async save(user: User): Promise<void> {
    // implementation
  }

  async delete(user: User): Promise<void> {
    // implementation
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ variance
const userRepo: IRepository<User> = new UserRepository(); // ‚úÖ OK
const adminRepo: IRepository<Admin> = userRepo; // ‚ùå –û—à–∏–±–∫–∞ (invariant –∏–∑-–∑–∞ IWriter)
```

### –û–ø–∏—Å–∞–Ω–∏–µ

TypeScript –∏—Å–ø–æ–ª—å–∑—É–µ—Ç **—Å—Ç—Ä—É–∫—Ç—É—Ä–Ω—É—é —Ç–∏–ø–∏–∑–∞—Ü–∏—é** (structural typing), –≥–¥–µ —Å–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç—å —Ç–∏–ø–æ–≤ –æ–ø—Ä–µ–¥–µ–ª—è–µ—Ç—Å—è —Å—Ç—Ä—É–∫—Ç—É—Ä–æ–π, –∞ –Ω–µ –∏–º–µ–Ω–µ–º. **–ë—Ä–µ–Ω–¥–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ —Ç–∏–ø—ã** (Branded Types) –∏ **–Ω–æ–º–∏–Ω–∞–ª—å–Ω—ã–µ –ø–∞—Ç—Ç–µ—Ä–Ω—ã** (Nominal Typing patterns) –ø–æ–∑–≤–æ–ª—è—é—Ç —Å–æ–∑–¥–∞–≤–∞—Ç—å —Ç–∏–ø—ã, –∫–æ—Ç–æ—Ä—ã–µ —Å—Ç—Ä—É–∫—Ç—É—Ä–Ω–æ –∏–¥–µ–Ω—Ç–∏—á–Ω—ã, –Ω–æ –ª–æ–≥–∏—á–µ—Å–∫–∏ —Ä–∞–∑–ª–∏—á–Ω—ã, –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–∞—è —Å–ª—É—á–∞–π–Ω–æ–µ —Å–º–µ—à–∏–≤–∞–Ω–∏–µ —Å–µ–º–∞–Ω—Ç–∏—á–µ—Å–∫–∏ —Ä–∞–∑–Ω—ã—Ö –∑–Ω–∞—á–µ–Ω–∏–π.

### –ü—Ä–æ–±–ª–µ–º–∞ —Å—Ç—Ä—É–∫—Ç—É—Ä–Ω–æ–π —Ç–∏–ø–∏–∑–∞—Ü–∏–∏

