### Best Practices

```typescript
// ‚úÖ –•–û–†–û–®–û: –°–æ–∑–¥–∞–≤–∞–π—Ç–µ –≤—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω—ã–µ —É—Ç–∏–ª–∏—Ç—ã –¥–ª—è –±—Ä–µ–Ω–¥–æ–≤
type Brand<T, BrandName extends string> = T & { __brand: BrandName };

function brand<T, B extends string>(value: T): Brand<T, B> {
  return value as Brand<T, B>;
}

function unbrand<T, B extends string>(branded: Brand<T, B>): T {
  return branded as T;
}

// ‚úÖ –•–û–†–û–®–û: –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ —Ñ–∞–±—Ä–∏–∫–∏ —Å –≤–∞–ª–∏–¥–∞—Ü–∏–µ–π
class BrandedId<Tag extends string> {
  private constructor(
    private readonly value: string,
    private readonly tag: Tag
  ) {}

  static create<Tag extends string>(
    tag: Tag,
    validator?: (value: string) => boolean
  ) {
    return (value: string): Brand<string, Tag> => {
      if (validator && !validator(value)) {
        throw new Error(`Invalid ${tag}: ${value}`);
      }
      return brand<string, Tag>(value);
    };
  }

  toString(): string {
    return this.value;
  }
}

// ‚úÖ –•–û–†–û–®–û: –ì—Ä—É–ø–ø–∏—Ä—É–π—Ç–µ —Å–≤—è–∑–∞–Ω–Ω—ã–µ –±—Ä–µ–Ω–¥—ã
namespace Ids {
  export type User = Brand<string, 'UserId'>;
  export type Order = Brand<string, 'OrderId'>;
  export type Product = Brand<string, 'ProductId'>;

  export const User = (id: string): User => brand(id);
  export const Order = (id: string): Order => brand(id);
  export const Product = (id: string): Product => brand(id);
}

// ‚úÖ –•–û–†–û–®–û: –î–æ–∫—É–º–µ–Ω—Ç–∏—Ä—É–π—Ç–µ –±—Ä–µ–Ω–¥–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ —Ç–∏–ø—ã
/**
 * Represents a validated email address.
 * Can only be created through EmailValidator.create()
 * which ensures proper format validation.
 */
type Email = Brand<string, 'Email'>;

/**
 * Represents a positive integer user ID.
 * Must be > 0 and a whole number.
 */
type UserId = Brand<number, 'UserId'>;

// ‚úÖ –•–û–†–û–®–û: –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ type guards –¥–ª—è runtime –ø—Ä–æ–≤–µ—Ä–æ–∫
function isUserId(value: unknown): value is UserId {
  return typeof value === 'number' &&
         Number.isInteger(value) &&
         value > 0;
}

function assertUserId(value: unknown): asserts value is UserId {
  if (!isUserId(value)) {
    throw new TypeError('Invalid UserId');
  }
}

// ‚úÖ –•–û–†–û–®–û: –ö–æ–º–±–∏–Ω–∏—Ä—É–π—Ç–µ —Å –¥—Ä—É–≥–∏–º–∏ –ø–∞—Ç—Ç–µ—Ä–Ω–∞–º–∏
type Result<T, E> =
  | { success: true; value: T }
  | { success: false; error: E };

function parseUserId(input: string): Result<UserId, string> {
  const num = parseInt(input, 10);
  if (isNaN(num) || num <= 0) {
    return { success: false, error: 'Invalid user ID format' };
  }
  return { success: true, value: brand<number, 'UserId'>(num) };
}

