### –ü—Ä–∞–∫—Ç–∏—á–µ—Å–∫–∏–π –ø—Ä–∏–º–µ—Ä: Banking System

```typescript
// üè¶ –ö–æ–º–ø–ª–µ–∫—Å–Ω—ã–π –ø—Ä–∏–º–µ—Ä: –±–∞–Ω–∫–æ–≤—Å–∫–∞—è —Å–∏—Å—Ç–µ–º–∞
declare const accountNumberBrand: unique symbol;
declare const sortCodeBrand: unique symbol;
declare const ibanBrand: unique symbol;

type AccountNumber = string & { [accountNumberBrand]: void };
type SortCode = string & { [sortCodeBrand]: void };
type IBAN = string & { [ibanBrand]: void };

class BankingValidators {
  static createAccountNumber(value: string): AccountNumber {
    if (!/^\d{8}$/.test(value)) {
      throw new Error('Account number must be 8 digits');
    }
    return value as AccountNumber;
  }

  static createSortCode(value: string): SortCode {
    if (!/^\d{2}-\d{2}-\d{2}$/.test(value)) {
      throw new Error('Sort code must be in format XX-XX-XX');
    }
    return value as SortCode;
  }

  static createIBAN(value: string): IBAN {
    const normalized = value.replace(/\s/g, '').toUpperCase();
    if (!/^[A-Z]{2}\d{2}[A-Z0-9]+$/.test(normalized)) {
      throw new Error('Invalid IBAN format');
    }
