```typescript
type PaymentMethod =
  | { type: 'cash'; amount: number }
  | { type: 'card'; cardNumber: string; amount: number }
  | { type: 'crypto'; wallet: string; currency: string; amount: number };

// ‚úÖ Switch statement - —á–∏—â–µ –∏ –±–µ–∑–æ–ø–∞—Å–Ω–µ–µ
function processPaymentSwitch(payment: PaymentMethod): string {
  switch (payment.type) {
    case 'cash':
      return `Cash payment: $${payment.amount}`;
    case 'card':
      return `Card ${payment.cardNumber}: $${payment.amount}`;
    case 'crypto':
      return `Crypto ${payment.currency} to ${payment.wallet}: $${payment.amount}`;
    default:
      return assertNever(payment);
  }
}

// ‚úÖ If-else - –±–æ–ª–µ–µ –≥–∏–±–∫–∏–π –¥–ª—è —Å–ª–æ–∂–Ω–æ–π –ª–æ–≥–∏–∫–∏
function processPaymentIfElse(payment: PaymentMethod): string {
  if (payment.type === 'cash') {
    if (payment.amount > 10000) {
      return `Large cash payment: $${payment.amount} - requires verification`;
    }
    return `Cash payment: $${payment.amount}`;
  } else if (payment.type === 'card') {
    const masked = payment.cardNumber.slice(-4).padStart(payment.cardNumber.length, '*');
    return `Card ${masked}: $${payment.amount}`;
  } else if (payment.type === 'crypto') {
    return `Crypto ${payment.currency} to ${payment.wallet}: $${payment.amount}`;
  } else {
    return assertNever(payment);
  }
}

// ‚ö° Hybrid approach - switch –¥–ª—è –æ—Å–Ω–æ–≤–Ω–æ–π –ª–æ–≥–∏–∫–∏, if –¥–ª—è —É—Å–ª–æ–≤–∏–π
function processPaymentHybrid(payment: PaymentMethod): string {
  switch (payment.type) {
    case 'cash':
      if (payment.amount > 10000) {
        return `Large cash payment: $${payment.amount} - verification required`;
      }
      return `Cash payment: $${payment.amount}`;

    case 'card':
      const isDebit = payment.cardNumber.startsWith('4');
      return `${isDebit ? 'Debit' : 'Credit'} card: $${payment.amount}`;

    case 'crypto':
      return `Crypto ${payment.currency}: $${payment.amount}`;

    default:
      return assertNever(payment);
  }
}
```

