### Pattern Matching Emulation üéØ

TypeScript –Ω–µ –∏–º–µ–µ—Ç –≤—Å—Ç—Ä–æ–µ–Ω–Ω–æ–≥–æ pattern matching, –Ω–æ –µ–≥–æ –º–æ–∂–Ω–æ —ç–º—É–ª–∏—Ä–æ–≤–∞—Ç—å:

```typescript
// –≠–º—É–ª—è—Ü–∏—è pattern matching —á–µ—Ä–µ–∑ –æ–±—ä–µ–∫—Ç —Ñ—É–Ω–∫—Ü–∏–π
type Result<T, E> =
  | { type: 'ok'; value: T }
  | { type: 'error'; error: E };

// ‚úÖ Pattern matching style —Å –æ–±—ä–µ–∫—Ç–æ–º handlers
function match<T, E, R>(
  result: Result<T, E>,
  handlers: {
    ok: (value: T) => R;
    error: (error: E) => R;
  }
): R {
  switch (result.type) {
    case 'ok':
      return handlers.ok(result.value);
    case 'error':
      return handlers.error(result.error);
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const apiResult: Result<string, Error> = { type: 'ok', value: 'Success!' };

const message = match(apiResult, {
  ok: (value) => `Got value: ${value}`,
  error: (err) => `Error occurred: ${err.message}`,
});

// ‚ö° –†–∞—Å—à–∏—Ä–µ–Ω–Ω—ã–π pattern matching —Å guards
type Option<T> =
  | { type: 'some'; value: T }
  | { type: 'none' };

function matchOption<T, R>(
  option: Option<T>,
  handlers: {
    some: (value: T) => R;
    none: () => R;
  }
): R {
  return option.type === 'some'
    ? handlers.some(option.value)
    : handlers.none();
}

// üî• –°–ª–æ–∂–Ω—ã–π pattern matching —Å –≤–ª–æ–∂–µ–Ω–Ω—ã–º–∏ —Å—Ç—Ä—É–∫—Ç—É—Ä–∞–º–∏
type Expression =
  | { type: 'number'; value: number }
  | { type: 'add'; left: Expression; right: Expression }
  | { type: 'multiply'; left: Expression; right: Expression };

function evaluate(expr: Expression): number {
  return match(expr, {
    number: (value) => value,
    add: (left, right) => evaluate(left) + evaluate(right),
    multiply: (left, right) => evaluate(left) * evaluate(right),
  });
}

function match<R>(
  expr: Expression,
  handlers: {
    number: (value: number) => R;
    add: (left: Expression, right: Expression) => R;
    multiply: (left: Expression, right: Expression) => R;
  }
): R {
  switch (expr.type) {
    case 'number':
      return handlers.number(expr.value);
    case 'add':
      return handlers.add(expr.left, expr.right);
    case 'multiply':
      return handlers.multiply(expr.left, expr.right);
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const expression: Expression = {
  type: 'add',
  left: { type: 'number', value: 5 },
  right: { type: 'multiply', left: { type: 'number', value: 3 }, right: { type: 'number', value: 4 } }
};

console.log(evaluate(expression)); // 5 + (3 * 4) = 17
```

### State Machines —Å Discriminated Unions üé∞

#### –†–µ–∞–ª–∏–∑–∞—Ü–∏—è –∫–æ–Ω–µ—á–Ω–æ–≥–æ –∞–≤—Ç–æ–º–∞—Ç–∞

```typescript
// –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Å–æ—Å—Ç–æ—è–Ω–∏–π –º–∞—à–∏–Ω—ã —Å–æ—Å—Ç–æ—è–Ω–∏–π
type TrafficLightState =
  | { state: 'red'; duration: number }
  | { state: 'yellow'; duration: number; nextState: 'red' | 'green' }
  | { state: 'green'; duration: number };

type TrafficLightEvent =
  | { type: 'TIMER' }
  | { type: 'EMERGENCY'; priority: number }
  | { type: 'MANUAL_OVERRIDE'; targetState: 'red' | 'yellow' | 'green' };

// State machine —Å transition logic
class TrafficLight {
  private currentState: TrafficLightState = { state: 'red', duration: 30 };

  getState(): TrafficLightState {
    return this.currentState;
  }

  transition(event: TrafficLightEvent): TrafficLightState {
    switch (event.type) {
      case 'TIMER':
        return this.handleTimer();
      case 'EMERGENCY':
        return this.handleEmergency(event.priority);
      case 'MANUAL_OVERRIDE':
        return this.handleManualOverride(event.targetState);
      default:
        return assertNever(event);
    }
  }

  private handleTimer(): TrafficLightState {
    switch (this.currentState.state) {
      case 'red':
        this.currentState = { state: 'green', duration: 45 };
        break;
      case 'yellow':
        if (this.currentState.nextState === 'red') {
          this.currentState = { state: 'red', duration: 30 };
        } else {
          this.currentState = { state: 'green', duration: 45 };
        }
        break;
      case 'green':
        this.currentState = { state: 'yellow', duration: 5, nextState: 'red' };
        break;
      default:
        assertNever(this.currentState);
    }
    return this.currentState;
  }

  private handleEmergency(priority: number): TrafficLightState {
    if (priority >= 5) {
      this.currentState = { state: 'red', duration: 60 };
    }
    return this.currentState;
  }

  private handleManualOverride(targetState: 'red' | 'yellow' | 'green'): TrafficLightState {
    switch (targetState) {
      case 'red':
        this.currentState = { state: 'red', duration: 30 };
        break;
      case 'yellow':
        this.currentState = { state: 'yellow', duration: 5, nextState: 'red' };
        break;
      case 'green':
        this.currentState = { state: 'green', duration: 45 };
        break;
    }
    return this.currentState;
  }
}

// üî• –°–ª–æ–∂–Ω–∞—è state machine –¥–ª—è –∑–∞–∫–∞–∑–∞
type OrderState =
  | { status: 'draft'; items: string[]; editableUntil: Date }
  | { status: 'pending_payment'; items: string[]; total: number; paymentUrl: string }
  | { status: 'paid'; items: string[]; total: number; paidAt: Date; transactionId: string }
  | { status: 'shipped'; items: string[]; total: number; trackingNumber: string }
  | { status: 'delivered'; items: string[]; total: number; deliveredAt: Date }
  | { status: 'cancelled'; items: string[]; reason: string; cancelledAt: Date }
  | { status: 'refunded'; items: string[]; total: number; refundedAmount: number; refundedAt: Date };

type OrderEvent =
  | { type: 'ADD_ITEM'; item: string }
  | { type: 'REMOVE_ITEM'; item: string }
  | { type: 'SUBMIT_ORDER' }
  | { type: 'PAYMENT_COMPLETED'; transactionId: string }
  | { type: 'SHIP_ORDER'; trackingNumber: string }
  | { type: 'MARK_DELIVERED' }
  | { type: 'CANCEL_ORDER'; reason: string }
  | { type: 'REFUND_ORDER'; amount: number };

class OrderStateMachine {
  constructor(private state: OrderState) {}

  getState(): OrderState {
    return this.state;
  }

  send(event: OrderEvent): OrderState {
    const newState = this.transition(this.state, event);
    this.state = newState;
    return newState;
  }

  private transition(state: OrderState, event: OrderEvent): OrderState {
    switch (state.status) {
      case 'draft':
        return this.transitionFromDraft(state, event);
      case 'pending_payment':
        return this.transitionFromPendingPayment(state, event);
      case 'paid':
        return this.transitionFromPaid(state, event);
      case 'shipped':
        return this.transitionFromShipped(state, event);
      case 'delivered':
        return this.transitionFromDelivered(state, event);
      case 'cancelled':
      case 'refunded':
        // Terminal states - no transitions allowed
        return state;
      default:
        return assertNever(state);
    }
  }

  private transitionFromDraft(state: Extract<OrderState, { status: 'draft' }>, event: OrderEvent): OrderState {
    switch (event.type) {
      case 'ADD_ITEM':
        return { ...state, items: [...state.items, event.item] };
      case 'REMOVE_ITEM':
        return { ...state, items: state.items.filter(item => item !== event.item) };
      case 'SUBMIT_ORDER':
        const total = state.items.length * 10; // Simplified
        return {
          status: 'pending_payment',
          items: state.items,
          total,
          paymentUrl: `https://payment.com/${Date.now()}`
        };
      case 'CANCEL_ORDER':
        return {
          status: 'cancelled',
          items: state.items,
          reason: event.reason,
          cancelledAt: new Date()
        };
      default:
        return state; // Invalid event for this state
    }
  }

  private transitionFromPendingPayment(
    state: Extract<OrderState, { status: 'pending_payment' }>,
    event: OrderEvent
  ): OrderState {
    switch (event.type) {
      case 'PAYMENT_COMPLETED':
        return {
          status: 'paid',
          items: state.items,
          total: state.total,
          paidAt: new Date(),
          transactionId: event.transactionId
        };
      case 'CANCEL_ORDER':
        return {
          status: 'cancelled',
          items: state.items,
          reason: event.reason,
          cancelledAt: new Date()
        };
      default:
        return state;
    }
  }

  private transitionFromPaid(state: Extract<OrderState, { status: 'paid' }>, event: OrderEvent): OrderState {
    switch (event.type) {
      case 'SHIP_ORDER':
        return {
          status: 'shipped',
          items: state.items,
          total: state.total,
          trackingNumber: event.trackingNumber
        };
      case 'REFUND_ORDER':
        return {
          status: 'refunded',
          items: state.items,
          total: state.total,
          refundedAmount: event.amount,
          refundedAt: new Date()
        };
      default:
        return state;
    }
  }

  private transitionFromShipped(state: Extract<OrderState, { status: 'shipped' }>, event: OrderEvent): OrderState {
    switch (event.type) {
      case 'MARK_DELIVERED':
        return {
          status: 'delivered',
          items: state.items,
          total: state.total,
          deliveredAt: new Date()
        };
      default:
        return state;
    }
  }

  private transitionFromDelivered(state: Extract<OrderState, { status: 'delivered' }>, event: OrderEvent): OrderState {
    switch (event.type) {
      case 'REFUND_ORDER':
        return {
          status: 'refunded',
          items: state.items,
          total: state.total,
          refundedAmount: event.amount,
          refundedAt: new Date()
        };
      default:
        return state;
    }
  }
}

