interface ApiResponse {
  status: 'success' | 'error';
  data?: unknown;
  error?: string;
}

async function fetchData(url: string): Promise<ApiResponse> {
  const response = await fetch(url);
  return response.json(); // ‚ö†Ô∏è –ù–µ—Ç –≥–∞—Ä–∞–Ω—Ç–∏–π, —á—Ç–æ —ç—Ç–æ ApiResponse!
}

// –î–∞–Ω–Ω—ã–µ –º–æ–≥—É—Ç –ø—Ä–∏–π—Ç–∏ –≤ –ª—é–±–æ–º —Ñ–æ—Ä–º–∞—Ç–µ:
// { status: 'pending' } - –Ω–µ–ø—Ä–∞–≤–∏–ª—å–Ω—ã–π status
// { data: 'result' } - –Ω–µ—Ç status
// "error message" - –≤–æ–æ–±—â–µ –Ω–µ –æ–±—ä–µ–∫—Ç
