```typescript
// ‚úÖ class-validator - –≤–∞–ª–∏–¥–∞—Ü–∏—è —á–µ—Ä–µ–∑ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä—ã
import {
  IsString,
  IsNumber,
  IsEmail,
  Min,
  Max,
  IsOptional,
  IsArray,
  ValidateNested,
  IsEnum
} from 'class-validator';
import { validate } from 'class-validator';
import { Type } from 'class-transformer';

enum UserRole {
  Admin = 'admin',
  User = 'user',
  Moderator = 'moderator'
}

class Address {
  @IsString()
  street: string;

  @IsString()
  city: string;

  @IsString()
  @IsOptional()
  country?: string;
}

class User {
  @IsNumber()
  @Min(1)
  id: number;

  @IsString()
  @Min(1)
  @Max(100)
  name: string;

  @IsEmail()
  email: string;

  @IsNumber()
  @Min(0)
  @Max(150)
  @IsOptional()
  age?: number;

  @IsEnum(UserRole)
  @IsArray()
  roles: UserRole[];

  @ValidateNested()
  @Type(() => Address)
  @IsOptional()
  address?: Address;
}

async function createUser(data: unknown): Promise<User> {
  const user = Object.assign(new User(), data);
  const errors = await validate(user);

  if (errors.length > 0) {
    throw new Error(`Validation failed: ${JSON.stringify(errors)}`);
  }

  return user;
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const userData = {
  id: 1,
  name: 'Alice',
  email: 'alice@example.com',
  age: 30,
  roles: [UserRole.User],
  address: {
    street: '123 Main St',
    city: 'New York'
  }
};

const user = await createUser(userData);

// üî• –ö–∞—Å—Ç–æ–º–Ω—ã–µ –≤–∞–ª–∏–¥–∞—Ç–æ—Ä—ã
import { registerDecorator, ValidationOptions, ValidationArguments } from 'class-validator';

function IsStrongPassword(validationOptions?: ValidationOptions) {
  return function (object: Object, propertyName: string) {
    registerDecorator({
      name: 'isStrongPassword',
      target: object.constructor,
      propertyName: propertyName,
      options: validationOptions,
      validator: {
        validate(value: any, args: ValidationArguments) {
          if (typeof value !== 'string') return false;

          const hasUpperCase = /[A-Z]/.test(value);
          const hasLowerCase = /[a-z]/.test(value);
          const hasNumbers = /\d/.test(value);
          const hasSpecialChar = /[!@#$%^&*]/.test(value);
          const isLongEnough = value.length >= 8;

          return hasUpperCase && hasLowerCase && hasNumbers && hasSpecialChar && isLongEnough;
        },
        defaultMessage(args: ValidationArguments) {
          return 'Password must be at least 8 characters long and contain uppercase, lowercase, numbers, and special characters';
        }
      }
    });
  };
}

class UserWithPassword extends User {
  @IsString()
  @IsStrongPassword()
  password: string;
}
