**–ü—Ä–∏–º–µ—Ä 1: Generic Repository Pattern**

```typescript
// –ë–∞–∑–æ–≤–∞—è –º–æ–¥–µ–ª—å
interface BaseModel {
  id: string;
  createdAt: Date;
  updatedAt: Date;
}

// Generic —Ä–µ–ø–æ–∑–∏—Ç–æ—Ä–∏–π
interface IRepository<T extends BaseModel> {
  findAll(): Promise<T[]>;
  findById(id: string): Promise<T | null>;
  create(data: Omit<T, keyof BaseModel>): Promise<T>;
  update(id: string, data: Partial<T>): Promise<T>;
  delete(id: string): Promise<boolean>;
}

// –†–µ–∞–ª–∏–∑–∞—Ü–∏—è
class Repository<T extends BaseModel> implements IRepository<T> {
  constructor(private storage: Map<string, T> = new Map()) {}

  async findAll(): Promise<T[]> {
    return Array.from(this.storage.values());
  }

  async findById(id: string): Promise<T | null> {
    return this.storage.get(id) || null;
  }

  async create(data: Omit<T, keyof BaseModel>): Promise<T> {
    const now = new Date();
    const id = Math.random().toString(36);
    const item = {
      ...data,
      id,
      createdAt: now,
      updatedAt: now
    } as T;

    this.storage.set(id, item);
    return item;
  }

  async update(id: string, data: Partial<T>): Promise<T> {
    const existing = await this.findById(id);
    if (!existing) {
      throw new Error(`Item with id ${id} not found`);
    }

    const updated = {
      ...existing,
      ...data,
      updatedAt: new Date()
    };

    this.storage.set(id, updated);
    return updated;
  }

  async delete(id: string): Promise<boolean> {
    return this.storage.delete(id);
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
interface Product extends BaseModel {
  name: string;
  price: number;
  category: string;
}

const productRepo = new Repository<Product>();

// –°–æ–∑–¥–∞–Ω–∏–µ
const product = await productRepo.create({
  name: "Laptop",
  price: 1000,
  category: "Electronics"
});

// –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ
await productRepo.update(product.id, { price: 900 });

// –ü–æ–ª—É—á–µ–Ω–∏–µ
const allProducts = await productRepo.findAll();
```

**–ü—Ä–∏–º–µ—Ä 2: Generic Event Emitter**

```typescript
// –¢–∏–ø—ã —Å–æ–±—ã—Ç–∏–π
interface EventMap {
  [event: string]: unknown;
}

// Generic Event Emitter
class TypedEventEmitter<TEvents extends EventMap> {
  private listeners = new Map<keyof TEvents, Set<Function>>();

  on<K extends keyof TEvents>(
    event: K,
    listener: (data: TEvents[K]) => void
  ): () => void {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, new Set());
    }

    this.listeners.get(event)!.add(listener);

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é –æ—Ç–ø–∏—Å–∫–∏
    return () => this.off(event, listener);
  }

  off<K extends keyof TEvents>(
    event: K,
    listener: (data: TEvents[K]) => void
  ): void {
    const eventListeners = this.listeners.get(event);
    if (eventListeners) {
      eventListeners.delete(listener);
    }
  }

  emit<K extends keyof TEvents>(event: K, data: TEvents[K]): void {
    const eventListeners = this.listeners.get(event);
    if (eventListeners) {
      eventListeners.forEach(listener => listener(data));
    }
  }

  once<K extends keyof TEvents>(
    event: K,
    listener: (data: TEvents[K]) => void
  ): void {
    const onceWrapper = (data: TEvents[K]) => {
      listener(data);
      this.off(event, onceWrapper);
    };
    this.on(event, onceWrapper);
  }
}

// –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Å–æ–±—ã—Ç–∏–π –ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è
