**–ü—Ä–∏–º–µ—Ä 1: Type-safe Form Builder**

```typescript
// –ë–∞–∑–æ–≤—ã–µ —Ç–∏–ø—ã –ø–æ–ª–µ–π
interface FieldBase {
  name: string;
  label: string;
  required?: boolean;
}

interface TextField extends FieldBase {
  type: "text";
  placeholder?: string;
  maxLength?: number;
}

interface NumberField extends FieldBase {
  type: "number";
  min?: number;
  max?: number;
  step?: number;
}

interface SelectField<T extends string = string> extends FieldBase {
  type: "select";
  options: readonly T[];
}

type Field = TextField | NumberField | SelectField;

// Generic Form Builder —Å constraints
type FormValues<T extends readonly Field[]> = {
  [K in T[number] as K["name"]]: K extends TextField
    ? string
    : K extends NumberField
      ? number
      : K extends SelectField<infer O>
        ? O
        : never;
};

class FormBuilder<T extends readonly Field[]> {
  constructor(private fields: T) {}

  validate(values: FormValues<T>): boolean {
    for (const field of this.fields) {
      const value = values[field.name as keyof FormValues<T>];

      if (field.required && !value) {
        return false;
      }

      if (field.type === "number") {
        const numField = field as NumberField;
        const numValue = value as number;

        if (numField.min !== undefined && numValue < numField.min) {
          return false;
        }
        if (numField.max !== undefined && numValue > numField.max) {
          return false;
        }
      }
    }

    return true;
  }

  getDefaultValues(): FormValues<T> {
    const defaults = {} as FormValues<T>;

    for (const field of this.fields) {
      const key = field.name as keyof FormValues<T>;

      if (field.type === "text") {
        (defaults as any)[key] = "";
      } else if (field.type === "number") {
        (defaults as any)[key] = 0;
      } else if (field.type === "select") {
        (defaults as any)[key] = (field as SelectField).options[0];
      }
    }

    return defaults;
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const userFormFields = [
  {
    type: "text" as const,
    name: "username",
    label: "Username",
    required: true,
    maxLength: 50
  },
  {
    type: "number" as const,
    name: "age",
    label: "Age",
    min: 18,
    max: 100
  },
  {
    type: "select" as const,
    name: "role",
    label: "Role",
    options: ["admin", "user", "guest"] as const
  }
] as const;

const formBuilder = new FormBuilder(userFormFields);

// –¢–∏–ø–æ–±–µ–∑–æ–ø–∞—Å–Ω—ã–µ –∑–Ω–∞—á–µ–Ω–∏—è —Ñ–æ—Ä–º—ã
const formValues = {
  username: "john_doe",
  age: 30,
  role: "admin" as const  // —Å—Ç—Ä–æ–≥–æ —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–æ
};

const isValid = formBuilder.validate(formValues);
const defaults = formBuilder.getDefaultValues();
// type: { username: string; age: number; role: "admin" | "user" | "guest" }
```

**–ü—Ä–∏–º–µ—Ä 2: Type-safe Query Builder**

```typescript
// –ë–∞–∑–æ–≤—ã–µ —Ç–∏–ø—ã –¥–ª—è Query Builder
interface Schema {
  [table: string]: {
    [column: string]: any;
  };
}

type WhereOperator = "=" | "!=" | ">" | "<" | ">=" | "<=" | "LIKE" | "IN";

interface WhereClause<T> {
  column: keyof T;
  operator: WhereOperator;
  value: any;
}

class QueryBuilder<
  S extends Schema,
  T extends keyof S = keyof S,
  Selected extends keyof S[T] = keyof S[T]
> {
  private tableName?: T;
  private selectedColumns: Selected[] = [];
  private whereClauses: WhereClause<S[T]>[] = [];
  private limitValue?: number;

  from<Table extends keyof S>(table: Table): QueryBuilder<S, Table, keyof S[Table]> {
    const qb = new QueryBuilder<S, Table, keyof S[Table]>();
    qb.tableName = table;
    return qb;
  }

  select<Cols extends keyof S[T]>(
    ...columns: Cols[]
  ): QueryBuilder<S, T, Cols> {
    const qb = new QueryBuilder<S, T, Cols>();
    qb.tableName = this.tableName;
    qb.selectedColumns = columns as any;
    qb.whereClauses = this.whereClauses;
    qb.limitValue = this.limitValue;
    return qb;
  }

  where<K extends keyof S[T]>(
    column: K,
    operator: WhereOperator,
    value: S[T][K]
  ): this {
    this.whereClauses.push({ column, operator, value } as any);
    return this;
  }

  limit(n: number): this {
    this.limitValue = n;
    return this;
  }

  build(): string {
    if (!this.tableName) {
      throw new Error("Table not specified");
    }

    const columns = this.selectedColumns.length > 0
      ? this.selectedColumns.join(", ")
      : "*";

    let query = `SELECT ${columns} FROM ${String(this.tableName)}`;

    if (this.whereClauses.length > 0) {
      const conditions = this.whereClauses
        .map(clause => `${String(clause.column)} ${clause.operator} ?`)
        .join(" AND ");
      query += ` WHERE ${conditions}`;
    }

    if (this.limitValue) {
      query += ` LIMIT ${this.limitValue}`;
    }

    return query;
  }

  // –¢–∏–ø–æ–±–µ–∑–æ–ø–∞—Å–Ω—ã–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç
  async execute(): Promise<Pick<S[T], Selected>[]> {
    const query = this.build();
    // –í—ã–ø–æ–ª–Ω–µ–Ω–∏–µ –∑–∞–ø—Ä–æ—Å–∞...
    return [] as Pick<S[T], Selected>[];
  }
}

// –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Å—Ö–µ–º—ã
interface MySchema extends Schema {
  users: {
    id: number;
    name: string;
    email: string;
    age: number;
    isActive: boolean;
  };
  posts: {
    id: number;
    userId: number;
    title: string;
    content: string;
    createdAt: Date;
  };
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const qb = new QueryBuilder<MySchema>();

const query = qb
  .from("users")
  .select("id", "name", "email")
  .where("age", ">=", 18)
  .where("isActive", "=", true)
  .limit(10);

const result = await query.execute();
// type: { id: number; name: string; email: string; }[]

// ‚ùå –û—à–∏–±–∫–∏ –∫–æ–º–ø–∏–ª—è—Ü–∏–∏
// qb.from("invalid_table");                    // Error: unknown table
// qb.from("users").select("invalid_column");   // Error: unknown column
// qb.from("users").where("age", "=", "text");  // Error: wrong type
```

