  query(sql: string) {
    console.log(`Executing: ${sql}`);
  }
}

// ‚úÖ Type-safe singleton factory
const dbFactory = new SingletonFactory<DatabaseConnection>(
  () => new DatabaseConnection('postgres://localhost'),
  'primary'
);

const db1 = dbFactory.getInstance(); // DatabaseConnection
const db2 = dbFactory.getInstance(); // Same instance!

console.log(db1 === db2); // true ‚úÖ
```

### –ü–∞—Ç—Ç–µ—Ä–Ω Factory Registry üìã

```typescript
// Registry –ø–∞—Ç—Ç–µ—Ä–Ω –¥–ª—è —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –º–Ω–æ–∂–µ—Å—Ç–≤–æ–º factories
type Factory<T> = {
  create: () => T;
};

class FactoryRegistry<T> {
  private factories = new Map<string, Factory<T>>();

  register(name: string, factory: Factory<T>): void {
    this.factories.set(name, factory);
  }

  create(name: string): T {
    const factory = this.factories.get(name);
    if (!factory) {
      throw new Error(`Factory '${name}' not found`);
    }
    return factory.create();
  }

  has(name: string): boolean {
    return this.factories.has(name);
  }

  listFactories(): string[] {
    return Array.from(this.factories.keys());
  }
}

// –ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è
