}

const transportRegistry = new FactoryRegistry<Transport>();

transportRegistry.register('car', {
  create: () => new Car()
});

transportRegistry.register('bike', {
  create: () => new Bike()
});

// ‚úÖ –î–∏–Ω–∞–º–∏—á–µ—Å–∫–æ–µ —Å–æ–∑–¥–∞–Ω–∏–µ
const transport1 = transportRegistry.create('car');
const transport2 = transportRegistry.create('bike');

