// Type-Safe DI Container
type ServiceDescriptor = {
  singleton: boolean;
  factory: (container: DIContainer) => any;
};

class DIContainer {
  private descriptors = new Map<symbol, ServiceDescriptor>();
  private singletons = new Map<symbol, any>();

  register<T>(
    token: symbol,
    factory: (container: this) => T,
    singleton: boolean = true
  ): this {
    this.descriptors.set(token, { factory, singleton });
    return this;
  }

  resolve<T>(token: symbol): T {
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º singleton
    if (this.singletons.has(token)) {
      return this.singletons.get(token);
    }

    const descriptor = this.descriptors.get(token);
    if (!descriptor) {
      throw new Error(`Service not registered: ${String(token)}`);
    }

    const instance = descriptor.factory(this) as T;

    if (descriptor.singleton) {
      this.singletons.set(token, instance);
    }

    return instance;
  }
}

// ‚úÖ Setup –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞
const container = new DIContainer();

container
  .register<Logger>(TYPES.Logger, () => new ConsoleLogger())
  .register<Database>(
    TYPES.Database,
    (c) => new PostgresDatabase(c.resolve(TYPES.Logger))
  )
  .register<UserService>(
    TYPES.UserService,
    (c) => new UserServiceImpl(
      c.resolve(TYPES.Database),
      c.resolve(TYPES.Logger)
    )
  )
  .register<AuthService>(
    TYPES.AuthService,
    (c) => new AuthServiceImpl(
      c.resolve(TYPES.UserService),
      c.resolve(TYPES.Logger)
    )
