### Decorators + Generic Factories üé≠

```typescript
// –ü–∞—Ä–∞–º–µ—Ç—Ä—ã –¥–ª—è registration
interface RegistrationOptions {
  singleton?: boolean;
  lazyload?: boolean;
}

// Decorator –¥–ª—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–π —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏
function Injectable(token: symbol, options?: RegistrationOptions) {
  return function<T extends { new (...args: any[]): {} }>(constructor: T) {
    // –ú–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ —Å–æ—Ö—Ä–∞–Ω—è—é—Ç—Å—è
    Reflect.defineMetadata('di:token', token, constructor);
    Reflect.defineMetadata('di:options', options || {}, constructor);
    return constructor;
  };
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
@Injectable(TYPES.Logger)
class ConsoleLoggerDecorated implements Logger {
  log(message: string) { console.log(`[LOG] ${message}`); }
  error(message: string) { console.error(`[ERROR] ${message}`); }
}

@Injectable(TYPES.Database)
class PostgresDatabaseDecorated implements Database {
  constructor(@Inject(TYPES.Logger) private logger: Logger) {}
  async connect() { this.logger.log('Connecting...'); }
  async query(sql: string) { return []; }
}

// Parameter decorator –¥–ª—è –∏–Ω—ä–µ–∫—Ü–∏–∏
function Inject(token: symbol) {
  return function(target: any, propertyKey: string | symbol | undefined, parameterIndex: number) {
    const existingMetadata = Reflect.getOwnMetadata('di:inject', target) || [];
    existingMetadata[parameterIndex] = token;
    Reflect.defineMetadata('di:inject', existingMetadata, target);
  };
}
