### –†–µ–∞–ª—å–Ω—ã–π –ø—Ä–∏–º–µ—Ä: Generic Pipeline üîÑ

```typescript
// –û–±—ä–µ–¥–∏–Ω—è–µ–º –≤—Å—ë –≤–º–µ—Å—Ç–µ –¥–ª—è –ø—Ä–∞–∫—Ç–∏—á–µ—Å–∫–æ–≥–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è
type ContainerType = 'array' | 'option' | 'promise';

abstract class Container<T, Kind extends ContainerType> {
  abstract kind: Kind;
  abstract map<U>(f: (t: T) => U): Container<U, Kind>;
  abstract flatMap<U>(f: (t: T) => Container<U, Kind>): Container<U, Kind>;
}

class ArrayContainer<T> extends Container<T, 'array'> {
  kind: 'array' = 'array';
  constructor(public value: T[]) { super(); }

  map<U>(f: (t: T) => U): ArrayContainer<U> {
    return new ArrayContainer(this.value.map(f));
  }

  flatMap<U>(f: (t: T) => Container<U, 'array'>): ArrayContainer<U> {
    return new ArrayContainer(
      this.value.flatMap(t => (f(t) as ArrayContainer<U>).value)
    );
  }
}

class OptionContainer<T> extends Container<T, 'option'> {
  kind: 'option' = 'option';
  constructor(public value: T | null) { super(); }

  map<U>(f: (t: T) => U): OptionContainer<U> {
    return new OptionContainer(this.value === null ? null : f(this.value));
  }

  flatMap<U>(f: (t: T) => Container<U, 'option'>): OptionContainer<U> {
    return new OptionContainer(
      this.value === null ? null : (f(this.value) as OptionContainer<U>).value
    );
  }
}

// ‚úÖ –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
