// 3. –ù—É–∂–Ω—ã –∫–æ–Ω—Å—Ç—Ä—É–∫—Ç–æ—Ä—ã —Å –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏–µ–π
abstract class Repository<T> {
  protected cache: Map<number, T> = new Map();

  constructor(protected dbConnection: string) {
    console.log(`Repository initialized with: ${dbConnection}`);
  }

  abstract findById(id: number): T | undefined;
}

class UserRepository extends Repository<User> {
  public findById(id: number): User | undefined {
