function LogMethod(
  target: any,
  propertyKey: string,
  descriptor: PropertyDescriptor
) {
  const originalMethod = descriptor.value;

  descriptor.value = function(...args: any[]) {
    console.log(`Calling ${propertyKey} with args:`, args);
    const result = originalMethod.apply(this, args);
    console.log(`Result:`, result);
    return result;
  };

  return descriptor;
}

class Calculator {
  @LogMethod
  public add(a: number, b: number): number {
    return a + b;
  }
}

const calc = new Calculator();
calc.add(5, 3);
// "Calling add with args: [5, 3]"
// "Result: 8"

// –î–µ–∫–æ—Ä–∞—Ç–æ—Ä –¥–ª—è –∏–∑–º–µ—Ä–µ–Ω–∏—è –≤—Ä–µ–º–µ–Ω–∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è
function TimeIt(
  target: any,
  propertyKey: string,
  descriptor: PropertyDescriptor
) {
  const originalMethod = descriptor.value;

  descriptor.value = async function(...args: any[]) {
    const start = Date.now();
    const result = await originalMethod.apply(this, args);
    const duration = Date.now() - start;
    console.log(`${propertyKey} took ${duration}ms`);
    return result;
  };

  return descriptor;
}

class DataService {
  @TimeIt
  async fetchData(id: number): Promise<any> {
    return new Promise(resolve => {
      setTimeout(() => resolve({ id, data: 'test' }), 100);
    });
  }
}

// –î–µ–∫–æ—Ä–∞—Ç–æ—Ä –¥–ª—è –≤–∞–ª–∏–¥–∞—Ü–∏–∏ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º–æ–≥–æ –∑–Ω–∞—á–µ–Ω–∏—è
function ValidateReturn(
  target: any,
  propertyKey: string,
  descriptor: PropertyDescriptor
) {
  const originalMethod = descriptor.value;

  descriptor.value = function(...args: any[]) {
    const result = originalMethod.apply(this, args);
    if (result === null || result === undefined) {
      throw new Error(
        `${propertyKey} returned invalid value: ${result}`
      );
    }
    return result;
  };

  return descriptor;
}

class UserRepository {
  @ValidateReturn
  public getUser(id: number): User {
    return new User(`User ${id}`);
  }
}
```

### Property Decorators (–î–µ–∫–æ—Ä–∞—Ç–æ—Ä—ã —Å–≤–æ–π—Å—Ç–≤)

Property decorator –ø–æ–ª—É—á–∞–µ—Ç target –∏ propertyKey:

```typescript
// –ü—Ä–æ—Å—Ç–æ–π –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä —Å–≤–æ–π—Å—Ç–≤–∞
function Validate(target: any, propertyKey: string) {
  console.log(`Property ${propertyKey} needs validation`);
}

class Person {
  @Validate
  name: string = '';

  @Validate
  email: string = '';
}

// –ë–æ–ª–µ–µ —Å–ª–æ–∂–Ω—ã–π –ø—Ä–∏–º–µ—Ä: –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ –∏–∑–º–µ–Ω–µ–Ω–∏–π
function Tracked(target: any, propertyKey: string) {
  let value: any;

  const getter = function() {
    return value;
  };

  const setter = function(newValue: any) {
    console.log(`${propertyKey} changed from ${value} to ${newValue}`);
    value = newValue;
  };

  Object.defineProperty(target, propertyKey, {
    get: getter,
    set: setter,
    enumerable: true,
    configurable: true
  });
}

class Settings {
  @Tracked
  theme: string = 'light';

  @Tracked
  language: string = 'en';
}

const settings = new Settings();
settings.theme = 'dark';      // "theme changed from undefined to dark"
settings.language = 'ru';     // "language changed from en to ru"
```

### Parameter Decorators (–î–µ–∫–æ—Ä–∞—Ç–æ—Ä—ã –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤)

Parameter decorator –ø–æ–ª—É—á–∞–µ—Ç target, propertyKey, parameterIndex:

```typescript
// –ü—Ä–æ—Å—Ç–æ–π –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä –ø–∞—Ä–∞–º–µ—Ç—Ä–∞
function Require(target: any, propertyKey: string, parameterIndex: number) {
  console.log(
    `Parameter ${parameterIndex} of ${propertyKey} is required`
  );
}

class UserService {
  public createUser(@Require name: string, age: number) {
    return new User(name);
  }
}

// –î–µ–∫–æ—Ä–∞—Ç–æ—Ä –¥–ª—è –≤–∞–ª–∏–¥–∞—Ü–∏–∏ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞
function ValidateParam(validator: (value: any) => boolean) {
  return function(target: any, propertyKey: string, parameterIndex: number) {
    const originalMethod = target[propertyKey];

    target[propertyKey] = function(...args: any[]) {
      if (!validator(args[parameterIndex])) {
        throw new Error(
          `Invalid parameter at index ${parameterIndex}`
        );
      }
      return originalMethod.apply(this, args);
    };
  };
}

class OrderService {
  public createOrder(
    @ValidateParam(x => typeof x === 'number' && x > 0) amount: number
  ) {
    return { id: 1, amount };
  }
}

// –£—Å–ø–µ—à–Ω—ã–π –≤—ã–∑–æ–≤
const order = new OrderService().createOrder(100);

// –û—à–∏–±–∫–∞
// new OrderService().createOrder(-50); // ‚ùå –û—à–∏–±–∫–∞ –≤–∞–ª–∏–¥–∞—Ü–∏–∏
```

### Metadata Reflection API

Metadata Reflection –ø–æ–∑–≤–æ–ª—è–µ—Ç —Ö—Ä–∞–Ω–∏—Ç—å –∏ –ø–æ–ª—É—á–∞—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ —Ç–∏–ø–∞—Ö:

```typescript
import 'reflect-metadata';

// –ü–æ–ª—É—á–µ–Ω–∏–µ –º–µ—Ç–∞–∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ —Ç–∏–ø–∞—Ö –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
function LogTypes(target: any, propertyKey: string) {
  const types = Reflect.getMetadata('design:paramtypes', target, propertyKey);
  const returnType = Reflect.getMetadata('design:returntype', target, propertyKey);

  console.log(`${propertyKey} params:`, types.map((t: any) => t.name));
  console.log(`${propertyKey} return type:`, returnType.name);
}

class MathService {
  @LogTypes
  public multiply(a: number, b: number): number {
    return a * b;
  }
}

// Output:
// multiply params: ['Number', 'Number']
// multiply return type: Number

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –º–µ—Ç–∞–¥–∞–Ω–Ω—ã—Ö –¥–ª—è DI
function Inject(target: any, propertyKey: string, descriptor: PropertyDescriptor) {
  const types = Reflect.getMetadata('design:paramtypes', target, propertyKey);

  const originalMethod = descriptor.value;
  descriptor.value = function(...args: any[]) {
    // –ú–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ —Ç–∏–ø–∞—Ö –¥–ª—è –≤–Ω–µ–¥—Ä–µ–Ω–∏—è –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–µ–π
    console.log('Method expects types:', types.map((t: any) => t.name));
    return originalMethod.apply(this, args);
  };

  return descriptor;
}

class DataProcessor {
  @Inject
  public process(data: string, logger: any): void {
    console.log('Processing:', data);
  }
}
```

### Use Case 1: Dependency Injection (DI)

```typescript
// –ü—Ä–æ—Å—Ç–∞—è DI –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä —Å –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä–∞–º–∏
class DIContainer {
  private static services: Map<string, any> = new Map();

  static register(name: string, service: any) {
    this.services.set(name, service);
  }

  static get(name: string) {
    return this.services.get(name);
  }
}

function Injectable(target: any) {
  const serviceName = target.name;
  DIContainer.register(serviceName, target);
}

function Inject(serviceName: string) {
  return function(target: any, propertyKey: string, descriptor?: PropertyDescriptor) {
    if (descriptor) {
      // –î–ª—è –º–µ—Ç–æ–¥–æ–≤
      const originalMethod = descriptor.value;
      descriptor.value = function(...args: any[]) {
        const dependency = DIContainer.get(serviceName);
        return originalMethod.apply(this, [dependency, ...args]);
      };
      return descriptor;
    }

    // –î–ª—è —Å–≤–æ–π—Å—Ç–≤
    Object.defineProperty(target, propertyKey, {
      get: () => DIContainer.get(serviceName)
    });
  };
}

@Injectable
class LoggerService {
  log(message: string) {
    console.log(`[LOG] ${message}`);
  }
}

@Injectable
class DatabaseService {
  connect() {
    console.log('Connected to database');
  }
}

class UserController {
  @Inject('LoggerService')
  logger!: LoggerService;

  constructor(
    @Inject('DatabaseService') private db: DatabaseService
  ) {}

  getUser(id: number) {
    this.logger.log(`Fetching user ${id}`);
    this.db.connect();
  }
}
```

### Use Case 2: Validation

```typescript
// –î–µ–∫–æ—Ä–∞—Ç–æ—Ä –¥–ª—è –≤–∞–ª–∏–¥–∞—Ü–∏–∏ —Å–≤–æ–π—Å—Ç–≤
function Required(target: any, propertyKey: string) {
  const validatingProperty = `_${propertyKey}`;

  Object.defineProperty(target, propertyKey, {
    get() {
      return this[validatingProperty];
    },
    set(value: any) {
      if (!value) {
        throw new Error(`${propertyKey} is required`);
      }
      this[validatingProperty] = value;
    }
  });
}

function MinLength(min: number) {
  return function(target: any, propertyKey: string) {
    const validatingProperty = `_${propertyKey}`;

    Object.defineProperty(target, propertyKey, {
      get() {
        return this[validatingProperty];
      },
      set(value: string) {
        if (value.length < min) {
          throw new Error(
            `${propertyKey} must be at least ${min} characters`
          );
        }
        this[validatingProperty] = value;
      }
    });
  };
}

function Validate(min: number, max: number) {
  return function(target: any, propertyKey: string) {
    const validatingProperty = `_${propertyKey}`;

    Object.defineProperty(target, propertyKey, {
      get() {
        return this[validatingProperty];
      },
      set(value: number) {
        if (value < min || value > max) {
          throw new Error(
            `${propertyKey} must be between ${min} and ${max}`
          );
        }
        this[validatingProperty] = value;
      }
    });
  };
}

class User {
  @Required
  @MinLength(3)
  name: string = '';

  @Validate(0, 150)
  age: number = 0;

  @Required
  @MinLength(5)
  email: string = '';
}

const user = new User();
user.name = 'John';       // ‚úì OK
user.age = 30;            // ‚úì OK
// user.age = 200;        // ‚ùå Error: age must be between 0 and 150
// user.name = 'Jo';      // ‚ùå Error: name must be at least 3 characters
```

### Use Case 3: Authorization

```typescript
function RequireRole(role: string) {
  return function(target: any, propertyKey: string, descriptor: PropertyDescriptor) {
    const originalMethod = descriptor.value;

    descriptor.value = function(...args: any[]) {
      const currentUser = (global as any).currentUser;

      if (!currentUser || !currentUser.roles.includes(role)) {
        throw new Error(`Access denied. Required role: ${role}`);
      }

      return originalMethod.apply(this, args);
    };

    return descriptor;
  };
}

class AdminController {
  @RequireRole('admin')
  deleteUser(id: number): void {
    console.log(`User ${id} deleted`);
  }

  @RequireRole('admin')
  updateSettings(settings: any): void {
    console.log('Settings updated');
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
(global as any).currentUser = { roles: ['admin'] };
const controller = new AdminController();
controller.deleteUser(1);  // ‚úì OK

(global as any).currentUser = { roles: ['user'] };
// controller.deleteUser(1); // ‚ùå Error: Access denied
```

