class ResourcePool {
  private static instance: ResourcePool | null = null;
  private resources: Map<string, any> = new Map();

  private constructor() {
    console.log('ResourcePool —Å–æ–∑–¥–∞–Ω');
  }

  static getInstance(): ResourcePool {
    if (!this.instance) {
      this.instance = new ResourcePool();
    }
    return this.instance;
