  validate(data: any): boolean;
  errors: string[];
}

function createCacheable(): WithCache {
  return {
    cache: new Map(),
    get(key) {
      return this.cache.get(key);
    },
    set(key, value) {
      this.cache.set(key, value);
    }
  };
}

function createValidatable(): WithValidation {
  return {
    errors: [],
    validate(data) {
      this.errors = [];
      if (!data || typeof data !== 'object') {
        this.errors.push('Data –¥–æ–ª–∂–Ω—ã –±—ã—Ç—å –æ–±—ä–µ–∫—Ç–æ–º');
        return false;
      }
      return true;
    }
  };
}

class UserRepository {
  private cache = createCacheable();
  private validator = createValidatable();

  findUser(id: string) {
    const cached = this.cache.get<any>(id);
    if (cached) return cached;

    const user = { id, name: 'Alice' };
    this.cache.set(id, user);
    return user;
  }

  validateUser(data: any): boolean {
    return this.validator.validate(data);
  }
}
