  name?: string;
  email?: string;
  age?: number;
}

// –° Partial:
type UserUpdate = Partial<User>;

// –ü—Ä–∏–º–µ–Ω–µ–Ω–∏–µ
const updateUser = (id: number, updates: Partial<User>) => {
  console.log(`Updating user ${id}`, updates);
};

updateUser(1, { name: "John" }); // ‚úì OK - —Ç–æ–ª—å–∫–æ name –æ–±–Ω–æ–≤–ª—è–µ—Ç—Å—è
updateUser(1, { name: "John", email: "john@example.com" }); // ‚úì OK
updateUser(1, {}); // ‚úì OK - –ø—É—Å—Ç–æ–π –æ–±—ä–µ–∫—Ç —Ä–∞–∑—Ä–µ—à–µ–Ω
```

**Use cases:**
- –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö (PATCH –∑–∞–ø—Ä–æ—Å—ã)
- –§–æ—Ä–º—ã —Å —á–∞—Å—Ç–∏—á–Ω—ã–º –∑–∞–ø–æ–ª–Ω–µ–Ω–∏–µ–º
- –ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–æ–Ω–Ω—ã–µ –æ–±—ä–µ–∫—Ç—ã —Å –¥–µ—Ñ–æ–ª—Ç–Ω—ã–º–∏ –∑–Ω–∞—á–µ–Ω–∏—è–º–∏

#### 2. **Required<T>** ‚Äî –í—Å–µ —Å–≤–æ–π—Å—Ç–≤–∞ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ

–ü—Ä–æ—Ç–∏–≤–æ–ø–æ–ª–æ–∂–Ω–æ—Å—Ç—å Partial ‚Äî –¥–µ–ª–∞–µ—Ç –≤—Å–µ —Å–≤–æ–π—Å—Ç–≤–∞ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–º–∏.

```typescript
// –†–µ–∞–ª–∏–∑–∞—Ü–∏—è
type Required<T> = {
